-- Kreiranje tablica ispočetka
CREATE TABLE Vlasnik (
  VlasnikID INT PRIMARY KEY,
  Ime VARCHAR(50),
  Prezime VARCHAR(50),
  Adresa VARCHAR(MAX)
);

CREATE TABLE Pas (
  PasID INT PRIMARY KEY,
  Ime VARCHAR(50),
  Starost INT,
  DatumUdomljenja DATE,
  DatumCijepljenja DATE,
  ZdravstvenoStanje VARCHAR(100),
  Spol CHAR(1),
  Pasmina VARCHAR(50),
  BojaDlake VARCHAR(50),
  FotografijaURL VARCHAR(MAX),
  Karakter VARCHAR(MAX),
  VlasnikID INT,
  FOREIGN KEY (VlasnikID) REFERENCES Vlasnik(VlasnikID)
);

CREATE TABLE Udomljenje (
    UdomljenjeID INT PRIMARY KEY IDENTITY(1,1),
    PasID INT NOT NULL,
    VlasnikID INT NOT NULL,
    DatumUdomljenja DATETIME NOT NULL,
    FOREIGN KEY (PasID) REFERENCES Pas(PasID),
    FOREIGN KEY (VlasnikID) REFERENCES Vlasnik(VlasnikID)
);






